  `status` int(3) DEFAULT '0',
  `theme_spot` varchar(200) DEFAULT '' COMMENT '景点',
  PRIMARY KEY (`theme_id`)
) ENGINE=InnoDB AUTO_INCREMENT=19 DEFAULT CHARSET=utf8 COMMENT='主题游表';

